import { Component, OnInit } from '@angular/core';
import { Store } from '@ngrx/store';
import { Observable } from 'rxjs';
import { getToDos, ToDoState } from './todos.selector';
import { addToDo } from '../todos/todos.actions';
import { ToDo } from './todos.selector';
import { v4 as uuidv4 } from 'uuid';

@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.scss']
})
export class TodosComponent implements OnInit {
  list$: Observable<Array<ToDo>>;
  todos: any[];
  newContent: string;
  newID: number;

  constructor(private store: Store<{ todoDetails }>) {
    this.list$ = store.select(getToDos);
  }

  ngOnInit(): void {
  }

  addToDo() {
    this.newID = uuidv4();
    // this.store.dispatch(addToDo({ content: this.newContent || 'Untitled'}));
    this.store.dispatch(addToDo({ id: this.newID, content: this.newContent || 'Untitled', completed: false }))
    this.newContent = '';
  }

}
<h1 class="text-center m-4">New To-Do</h1>
<form (ngSubmit)="addToDo()" class="flex justify-center">
    <input 
        class="overflow-ellipsis overflow-hidden font-sans border border-transparent m-4 w-42 py-3 px-6 rounded shadow-lg focus:outline-none focus:ring-2 focus:ring-purple-600 focus:border-transparent focus:placeholder-gray-300 placeholder-gray-500 placeholder-opacity-75"   
        placeholder="Type Your To Do Here!"
        type="text"
        [(ngModel)]="newContent"
    >
    <button    
        class="font-sans shadow-lg m-4 w-42 py-3 px-6 text-white rounded-lg bg-purple-600 hover:bg-purple-700 shadow-lg block md:inline-block"
        type="submit"
    >
        Add
    </button>
</form>
<h1 class="text-center m-4">To Do List</h1>
