import { Component, OnInit } from '@angular/core';
import { Store } from '@ngrx/store';
import { Observable } from 'rxjs';
import { getToDos, ToDoState } from './todos.selector';
import { addToDo } from '../todo/todo.actions';
import { ToDo } from './todos.selector';

@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.scss']
})
export class TodosComponent implements OnInit {
  list$: Observable<ToDo[]>
  newContent: string;

  constructor(private store: Store<{ }>) {
    this.list$ = store.select(getToDos);
  }

  ngOnInit(): void {
  }

  addToDo() {
    this.store.dispatch(addToDo({ content: this.newContent || 'Untitled'}));
    this.newContent = '';
  }

}
